graphql.query:
query NewSearchTeachersQuery($query: TeacherSearchQuery!) {
  s0: newSearch {
    teachers(query: $query, first: 50) {
      didFallback
      edges {
        cursor
        node {
          id
          legacyId
          firstName
          lastName
          school {
            legacyId
            name
            id
          }
          avgRatingRounded
          avgDifficultyRounded
          numRatings
          wouldTakeAgainPercentRounded
          mandatoryAttendance {
            yes
            no
            neither
            total
          }
          ratingsDistribution {
            r1
            r2
            r3
            r4
            r5
            total
          }
          ratings(first: 100) {
            edges {
              node {
                comment
                qualityRating
                difficultyRatingRounded
              }
            }
          }
        }
      }
    }
  }
}
